package ohmygift.member.db;public class MemberBean {	private String name;	private int age;	private String roll;		public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public int getAge() {		return age;	}	public void setAge(int age) {		this.age = age;	}	public String getRoll() {		return roll;	}	public void setRoll(String roll) {		this.roll = roll;	}	}