package ohmygift.rollingpaper.db;import java.util.Date;public class GiftInfoBean {	private String gift_image;	private String gift_name;	private String gift_link;	private int gift_price;	private String gift_type;	private Date gift_date;	public String getGift_image() {		return gift_image;	}	public void setGift_image(String gift_image) {		this.gift_image = gift_image;	}	public String getGift_name() {		return gift_name;	}	public void setGift_name(String gift_name) {		this.gift_name = gift_name;	}	public String getGift_link() {		return gift_link;	}	public void setGift_link(String gift_link) {		this.gift_link = gift_link;	}	public int getGift_price() {		return gift_price;	}	public void setGift_price(int gift_price) {		this.gift_price = gift_price;	}	public String getGift_type() {		return gift_type;	}	public void setGift_type(String gift_type) {		this.gift_type = gift_type;	}	public Date getGift_date() {		return gift_date;	}	public void setGift_date(Date gift_date) {		this.gift_date = gift_date;	}}