package ohmygift.rollingpaper.db;public class MessageBean {	private String messagelog_id = "";	private String member_id = "";	private String message ="";	private String date="";	private String photo_url="";	public String getMessagelog_id() {		return messagelog_id;	}	public void setMessagelog_id(String messagelog_id) {		this.messagelog_id = messagelog_id;	}	public String getMember_id() {		return member_id;	}	public void setMember_id(String member_id) {		this.member_id = member_id;	}	public String getMessage() {		return message;	}	public void setMessage(String message) {		this.message = message;	}	public String getDate() {		return date;	}	public void setDate(String date) {		this.date = date;	}	public String getPhoto_url() {		return photo_url;	}	public void setPhoto_url(String photo_url) {		this.photo_url = photo_url;	}}